function fl_SwipeToGoToNextPreviousFrame(event:TransformGestureEvent):void
{
	if(event.offsetX == 1)
	{
		// swiped right
		prevFrame();
	}
	else if(event.offsetX == -1)
	{
		// swiped left
		nextFrame();
	}
}

